mapper.insert:
insert into choice_question (id, couse_id, content, 
  op1, op2, op3, op4, 
  answer, resolution, score
  )
values (#{id,jdbcType=INTEGER}, #{couseId,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, 
  #{op1,jdbcType=CHAR}, #{op2,jdbcType=CHAR}, #{op3,jdbcType=CHAR}, #{op4,jdbcType=CHAR}, 
  #{answer,jdbcType=INTEGER}, #{resolution,jdbcType=VARCHAR}, #{score,jdbcType=DOUBLE}
  )

mapper.select:
select id, couse_id, content, op1, op2, op3, op4, answer, resolution, score
from choice_question
where id = #{id,jdbcType=INTEGER}
